INSERT INTO config_info VALUES (1, 'xuxiaowei.yml', 'DEFAULT_GROUP', 'server:\n  forward-headers-strategy: FRAMEWORK\n\nxuxiaowei:\n  cloud:\n    security:\n      private-key: \n      public-key: \n\nspring:\n  datasource:\n    url: jdbc:p6spy:mysql://${XUXIAOWEI_DATABASE_HOST:127.0.0.1}:${XUXIAOWEI_DATABASE_PORT:3306}/${XUXIAOWEI_DATABASE:spring_cloud_xuxiaowei}?useSSL=false&allowPublicKeyRetrieval=true&serverTimezone=GMT%2B8\n    driver-class-name: com.p6spy.engine.spy.P6SpyDriver\n    username: ${XUXIAOWEI_DATABASE_USERNAME:root}\n    password: ${XUXIAOWEI_DATABASE_PASSWORD:xuxiaowei.com.cn}\n\nlogging:\n  level:\n    cloud.xuxiaowei: debug\n    org.apache.coyote.http11.Http11InputBuffer: debug\n    org.springframework.cloud.gateway.handler.RoutePredicateHandlerMapping: debug\n    org.springframework.boot.autoconfigure.web.reactive.error.AbstractErrorWebExceptionHandler: debug\n    org.springframework.core.codec.CharSequenceEncoder: debug\n  pattern:\n    console: \"%clr(%d{${LOG_DATEFORMAT_PATTERN:yyyy-MM-dd HH:mm:ss.SSS}}){faint} %clr(${LOG_LEVEL_PATTERN:-%5p}) %clr(${PID:- }){magenta} %clr(---){faint} %clr([%15.15t]){faint} %clr(%-40.40logger{39}){cyan} %clr(:){faint} %X{C-Request-Id} %clr(:){faint} %X{C-IP} %clr(:){faint} %m%n${LOG_EXCEPTION_CONVERSION_WORD:%wEx}\"\n    file: \"%d{${LOG_DATEFORMAT_PATTERN:yyyy-MM-dd HH:mm:ss.SSS}} ${LOG_LEVEL_PATTERN:-%5p} ${PID:- } --- [%t] %-40.40logger{39} : %X{C-Request-Id} : %X{C-IP} : %m%n${LOG_EXCEPTION_CONVERSION_WORD:%wEx}\"\n  file:\n    name: /logs/${spring.application.name}/${spring.application.name}.log\n  logback:\n    rollingpolicy:\n      max-file-size: 50MB\n      max-history: 365\n      file-name-pattern: ${LOG_FILE}.%d{yyyy-MM-dd}.%i.log.gz\n\nmanagement:\n  endpoints:\n    web:\n      exposure:\n        include: \'*\'\n\nspringdoc:\n  swagger-ui:\n    default-models-expand-depth: 20\n    default-model-expand-depth: 20\n    oauth:\n      clientId: messaging-client\n      clientSecret: secret\n  oAuthFlow:\n